// src/components/Products.tsx
'use client';

import { useState } from 'react';
import {
    Container,
    Grid,
    Typography,
    Box,
    Card,
    CardMedia,
    CardContent,
    CardActions,
    Button,
    Chip,
    Rating,
    ToggleButtonGroup,
    ToggleButton,
    useTheme
} from '@mui/material';
import { AddShoppingCart, Favorite, FavoriteBorder } from '@mui/icons-material';

interface Product {
    id: number;
    name: string;
    price: number;
    image: string;
    description: string;
    category: string;
    rating: number;
    discount?: number;
    featured?: boolean;
}

interface ProductsProps {
    onAddToCart: (product: Product) => void;
}

const Products = ({ onAddToCart }: ProductsProps) => {
    const theme = useTheme();
    const [category, setCategory] = useState('all');
    const [favorites, setFavorites] = useState<number[]>([]);

    const products: Product[] = [
        {
            id: 1,
            name: 'ساعت مچی لوکس',
            price: 12500000,
            image: 'data:image/jpeg;base64,',
            description: 'ساعت مچی مردانه با طراحی مدرن و کیفیت عالی',
            category: 'اکسسوری',
            rating: 4.8,
            discount: 15,
            featured: true,
        },
        {
            id: 2,
            name: 'هدفون بی‌سیم',
            price: 3500000,
            image: 'data:image/jpeg;base64,',
            description: 'هدفون با کیفیت صدای استثنایی و طراحی ارگونومیک',
            category: 'الکترونیک',
            rating: 4.5,
            featured: true,
        },
        {
            id: 3,
            name: 'عینک آفتابی',
            price: 1800000,
            image: 'data:image/jpeg;base64,',
            description: 'عینک آفتابی با محافظت UV400 و طراحی شیک',
            category: 'اکسسوری',
            rating: 4.3,
        },
        {
            id: 4,
            name: 'دوربین حرفه‌ای',
            price: 28500000,
            image: 'data:image/jpeg;base64,',
            description: 'دوربین DSLR حرفه‌ای با قابلیت‌های پیشرفته',
            category: 'الکترونیک',
            rating: 4.9,
            discount: 10,
        },
        {
            id: 5,
            name: 'کیف چرمی',
            price: 4500000,
            image: 'data:image/jpeg;base64,',
            description: 'کیف چرم طبیعی با دوخت دقیق و طراحی کلاسیک',
            category: 'اکسسوری',
            rating: 4.6,
        },
        {
            id: 6,
            name: 'اسپیکر بلوتوث',
            price: 3200000,
            image: 'data:image/jpeg;base64,',
            description: 'اسپیکر پرتابل با صدای پر قدرت و باتری ماندگار',
            category: 'الکترونیک',
            rating: 4.4,
            discount: 20,
        },
    ];

    const categories = ['all', 'اکسسوری', 'الکترونیک'];

    const filteredProducts = category === 'all'
        ? products
        : products.filter(product => product.category === category);

    const toggleFavorite = (id: number) => {
        setFavorites(prev =>
            prev.includes(id)
                ? prev.filter(favId => favId !== id)
                : [...prev, id]
        );
    };

    return (
        <Box id="products" sx={{ py: 10, backgroundColor: theme.palette.background.paper }}>
            <Container maxWidth="lg">
                <Box textAlign="center" mb={6}>
                    <Typography variant="h2" component="h2" gutterBottom>
                        محصولات منتخب
                    </Typography>
                    <Typography variant="h6" color="textSecondary">
                        کشف کنید بهترین‌ها را از میان مجموعه‌های منتخب ما
                    </Typography>
                </Box>

                <Box sx={{ display: 'flex', justifyContent: 'center', mb: 6 }}>
                    <ToggleButtonGroup
                        value={category}
                        exclusive
                        onChange={(_, newCategory) => newCategory && setCategory(newCategory)}
                        sx={{
                            backgroundColor: theme.palette.background.default,
                            borderRadius: 3,
                            p: 1,
                        }}
                    >
                        {categories.map(cat => (
                            <ToggleButton
                                key={cat}
                                value={cat}
                                sx={{
                                    px: 4,
                                    py: 1,
                                    borderRadius: 2,
                                    textTransform: 'none',
                                    fontWeight: 500,
                                    '&.Mui-selected': {
                                        backgroundColor: theme.palette.primary.main,
                                        color: 'white',
                                    }
                                }}
                            >
                                {cat === 'all' ? 'همه' : cat}
                            </ToggleButton>
                        ))}
                    </ToggleButtonGroup>
                </Box>

                <Grid container spacing={4}>
                    {filteredProducts.map((product) => (
                        <Grid size={{ xs: 12, md: 4, sm: 6 }} key={product.id}>
                            <Card
                                sx={{
                                    height: '100%',
                                    display: 'flex',
                                    flexDirection: 'column',
                                    position: 'relative',
                                }}
                            >
                                {product.discount && (
                                    <Chip
                                        label={`${product.discount}% تخفیف`}
                                        color="secondary"
                                        sx={{
                                            position: 'absolute',
                                            top: 16,
                                            left: 16,
                                            zIndex: 1,
                                            fontWeight: 'bold',
                                        }}
                                    />
                                )}

                                <Box sx={{ position: 'absolute', top: 16, right: 16, zIndex: 1 }}>
                                    <Button
                                        size="small"
                                        onClick={() => toggleFavorite(product.id)}
                                        sx={{ minWidth: 'auto', p: 1 }}
                                    >
                                        {favorites.includes(product.id) ? (
                                            <Favorite color="error" />
                                        ) : (
                                            <FavoriteBorder />
                                        )}
                                    </Button>
                                </Box>

                                <CardMedia
                                    component="img"
                                    height="250"
                                    image={product.image}
                                    alt={product.name}
                                    sx={{ objectFit: 'cover' }}
                                />

                                <CardContent sx={{ flexGrow: 1 }}>
                                    <Typography variant="h6" component="h3" gutterBottom>
                                        {product.name}
                                    </Typography>
                                    <Typography variant="body2" color="textSecondary" paragraph>
                                        {product.description}
                                    </Typography>
                                    <Box sx={{ display: 'flex', alignItems: 'center', mb: 2 }}>
                                        <Rating value={product.rating} readOnly precision={0.1} />
                                        <Typography variant="body2" color="textSecondary" sx={{ ml: 1 }}>
                                            ({product.rating})
                                        </Typography>
                                    </Box>
                                    <Chip
                                        label={product.category}
                                        size="small"
                                        variant="outlined"
                                        sx={{ mb: 2 }}
                                    />
                                </CardContent>

                                <CardActions sx={{ p: 3, pt: 0 }}>
                                    <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', width: '100%' }}>
                                        <Box>
                                            <Typography variant="h6" color="primary">
                                                {product.price.toLocaleString()} تومان
                                            </Typography>
                                            {product.discount && (
                                                <Typography variant="body2" color="textSecondary" sx={{ textDecoration: 'line-through' }}>
                                                    {(product.price / (1 - product.discount / 100)).toLocaleString()} تومان
                                                </Typography>
                                            )}
                                        </Box>
                                        <Button
                                            variant="contained"
                                            startIcon={<AddShoppingCart />}
                                            onClick={() => onAddToCart(product)}
                                            sx={{
                                                borderRadius: 2,
                                                px: 3,
                                            }}
                                        >
                                            افزودن
                                        </Button>
                                    </Box>
                                </CardActions>
                            </Card>
                        </Grid>
                    ))}
                </Grid>
            </Container>
        </Box>
    );
};

export default Products;